firebase.firestore().collection("public/news/main").orderBy("year").get().then((async a=>{await a.forEach((a=>{newsData=a.data(),$(".navbar_section").html($(".navbar_section").html()+`<h4 class="header mt-2">${newsData.name} (${newsData.year})</h4><div class="content" id=${a.id}></div>`);for(let e=0;e<newsData.link.length;e++)$.ajax("https://opengraph.io/api/1.0/site/"+encodeURIComponent(newsData.link[e])+"?app_id=e234bff1-96f3-42cb-8dcb-1ea1c6130924").done((function(e){description=e.openGraph.description?e.openGraph.description:"",$(`.content#${a.id}`).html($(`.content#${a.id}`).html()+`<div class="card mb-3"><div class="row g-0"><div class="col-md-5"><img src="${e.openGraph.image.url}" class="img-fluid rounded-start" alt="${newsData.name}"></div><div class="col-md-7"><div class="card-body"><h5 class="card-title">${e.openGraph.title}</h5>\n                <p class="card-text">${description}</p><a class="card-link" href="${e.openGraph.url}" target="_blank"><i>Browse News</i></a></div></div></div></div>`)})).catch((a=>function(){console.error("Error fetching data from openGraph:",a)}))}))})).catch((a=>{console.error("Error getting list of documents inside news:",a)}));